import { Component, OnInit, HostListener } from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  currentUser: any = null;
  searchTerm: string = '';
  activeTab: string = 'explorar';
  sidebarOpen: boolean = false;
  
  restaurants = [
    {
      id: 1,
      name: 'Macchiata',
      cuisine: 'Brunch-Pizza-Pasta',
      address: 'La Primavera 1 en Cumbayá, Ecuador',
      rating: 4.5,
      reviews: 128,
      image: 'assets/Macchiata.png',
      favorite: false
    },
    {
      id: 2,
      name: 'Michael´s',
      cuisine: 'Grill',
      address: 'Quito, Guayaquil',
      rating: 4.8,
      reviews: 93,
      image: 'assets/Michaels.png',
      favorite: false
    },
    {
      id: 3,
      name: 'Roll It',
      cuisine: 'Sushi',
      address: 'Plaza Mayor 8, Valencia',
      rating: 4.3,
      reviews: 75,
      image: 'assets/RollIt.png',
      favorite: false
    }
  ];

  constructor(private authService: AuthService, private router: Router) {}

  ngOnInit() {
    this.currentUser = this.authService.getCurrentUser();
  }

  setActiveTab(tab: string) {
    this.activeTab = tab;
  }

  toggleFavorite(restaurant: any) {
    restaurant.favorite = !restaurant.favorite;
  }

  getFilteredRestaurants() {
    if (!this.searchTerm) {
      return this.restaurants;
    }
    return this.restaurants.filter(restaurant => 
      restaurant.name.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
      restaurant.cuisine.toLowerCase().includes(this.searchTerm.toLowerCase())
    );
  }

  getStars(rating: number): string[] {
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;
    const stars: string[] = [];
    
    for (let i = 0; i < fullStars; i++) {
      stars.push('★');
    }
    
    if (hasHalfStar) {
      stars.push('☆');
    }
    
    while (stars.length < 5) {
      stars.push('☆');
    }
    
    return stars;
  }

  // Sidebar methods
  toggleSidebar() {
    this.sidebarOpen = !this.sidebarOpen;
  }

  closeSidebar() {
    this.sidebarOpen = false;
  }

  navigateToSection(section: string) {
    this.closeSidebar();
    // Implement navigation logic based on section
    switch(section) {
      case 'foodie':
        this.setActiveTab('explorar');
        break;
      case 'restaurante':
        // Navigate to restaurant management (if implemented)
        console.log('Navigating to restaurant section');
        break;
      case 'cuenta':
        this.setActiveTab('perfil');
        break;
    }
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  // Listen for escape key to close sidebar
  @HostListener('document:keydown.escape', ['$event'])
  onEscapeKey(event: KeyboardEvent) {
    if (this.sidebarOpen) {
      this.closeSidebar();
    }
  }
}
<div class="dashboard-container">
  <!-- Header -->
  <header class="header">
    <div class="header-left">
      <button class="hamburger-menu" (click)="toggleSidebar()" [class.active]="sidebarOpen">
        <span class="hamburger-line"></span>
        <span class="hamburger-line"></span>
        <span class="hamburger-line"></span>
      </button>
      <img src="assets/logo.png" alt="FoodiesBNB Logo" class="header-logo">
    </div>
  </header>

  <!-- Sidebar Menu -->
  <div class="sidebar-overlay" [class.active]="sidebarOpen" (click)="closeSidebar()"></div>
  <nav class="sidebar" [class.active]="sidebarOpen">
    <div class="sidebar-logo-section">
      <img src="assets/logo.png" alt="FoodiesBNB Logo" class="sidebar-logo">
    </div>
    <ul class="sidebar-menu">
      <li class="menu-item">
        <a href="#" class="menu-link" (click)="navigateToSection('foodie')">
          <span class="menu-icon">🍽️</span>
          <span class="menu-text">Foodie</span>
        </a>
      </li>
      <li class="menu-item">
        <a href="#" class="menu-link" (click)="navigateToSection('restaurante')">
          <span class="menu-icon">🏪</span>
          <span class="menu-text">Restaurante</span>
        </a>
      </li>
      <li class="menu-item">
        <a href="#" class="menu-link" (click)="navigateToSection('cuenta')">
          <span class="menu-icon">👤</span>
          <span class="menu-text">Cuenta</span>
        </a>
      </li>
    </ul>
    <div class="sidebar-footer">
      <button class="logout-btn" (click)="logout()">
        <span class="menu-icon">🚪</span>
        <span class="menu-text">Cerrar Sesión</span>
      </button>
    </div>
  </nav>

  <!-- Main Content -->
  <main class="main-content">
    <div class="content-header">
      <h1>Restaurantes FoodiesBNB</h1>
      <p class="subtitle">Explora y conecta con restaurantes locales.</p>
    </div>

    <!-- Navigation Tabs -->
    <div class="navigation-tabs">
      <button class="tab-button active" (click)="setActiveTab('explorar')">
        Explorar Restaurantes
      </button>
    </div>

    <!-- Search and Filters -->
    <div class="search-filters-section">
      <div class="search-container">
        <div class="search-input-wrapper">
          <input type="text" placeholder="Buscar restaurantes por nombre..." [(ngModel)]="searchTerm">
          <span class="search-icon">🔍</span>
        </div>
      </div>
      <div class="filters-container">
        <div class="filter-group">
          <span class="location-icon">📍</span>
          <select class="filter-select">
            <option value="">Ubicación</option>
            <option value="madrid">Madrid</option>
            <option value="barcelona">Barcelona</option>
            <option value="valencia">Valencia</option>
            <option value="sevilla">Sevilla</option>
          </select>
        </div>
        <div class="filter-group">
          <span class="filter-icon">🍽️</span>
          <select class="filter-select">
            <option value="">Tipo de cocina</option>
            <option value="mexicano">Mexicano</option>
            <option value="japones">Japonés</option>
            <option value="italiano">Italiano</option>
            <option value="cafe">Café & Postres</option>
          </select>
        </div>
      </div>
    </div>

    <!-- Restaurants Grid -->
    <div class="restaurants-grid">
      <div class="restaurant-card" *ngFor="let restaurant of getFilteredRestaurants()">
        <div class="card-image">
          <img [src]="restaurant.image" [alt]="restaurant.name">
          <button class="favorite-btn" (click)="toggleFavorite(restaurant)" 
                  [class.active]="restaurant.favorite">
            <span class="heart-icon">♡</span>
          </button>
        </div>
        <div class="card-content">
          <h3 class="restaurant-name">{{ restaurant.name }}</h3>
          <p class="cuisine-type">{{ restaurant.cuisine }}</p>
          <p class="restaurant-address">{{ restaurant.address }}</p>
        </div>
      </div>
    </div>
  </main>
</div>
